number = int(input())

def positivoOuNegativo(number):
     if number<0:
         return "NEGATIVO"
     elif number>0:
         return "POSITIVO"
     else:
        return "ZERO"


print(positivoOuNegativo(number))

#include <iostream>
using namespace std;

int main()
{
    int days=0, month;

    cin >> month;

    switch(month)
    {
        case 1: case 3: case 5: case 7:
        case 8: case 10: case 12:
            days = days + 1;

        case 4: case 6:
        case 9: case 11:
            days = days + 2;

        case 2:
            days=days+28;
            break;
        default:
            cout <<"Mês inválido"<<endl;
            return 0;
    }

    cout << days;
}

